import React, { useEffect } from "react";
import { useContext, useState} from "react";
import { GlobalState } from "../../GlobalState";
import { Link } from "react-router-dom";
import "./Cart.css"

const Cart = () => {
  const state = useContext(GlobalState);
  const [cart] = state.userApi.cart;
  const [total, setTotal] = useState(0);

  useEffect(() => {
      const getTotal = () => {
          const total = cart.reduce((prev, item) => {
              return prev + (item.precio * item.quantity)
          }, 0)

          setTotal(total)
      }

      getTotal()
  }, [cart])



  if (cart.length === 0)
    return (
      <h2 style={{ textAlign: "center", fontSize: "4rem" }}> Cart Empty </h2>
    );
  return (
    <div>
      {cart.map((product) => (
        <div className="detail" key={product._id}>
          <img src={product.imagen.url} alt="" />

          <div className="box-detail">  
            <div className="row">
              <h2>{product.nombre}</h2>
            </div>

            <h3>s/.{product.precio * product.quantity}</h3>
            <p>{product.descripcion}</p>
            <p>{product.contenido}</p>

            <div className="amount">
                <button>
                    -
                </button>
                <span> {product.quantity} </span>
                <button>
                    +
                </button>
            </div>
            {/* <p>Sold: {product.sold}</p> */}
            {/* <Link to="/cart" className="cart">
              Comprar
            </Link> */}
            <div className="delete"> x </div>
          </div>
        </div>
      ))}

      <div className="total">
          <h3>s/. {total} </h3>
          <Link to="#!"> Pagar </Link>
      </div>
    </div>
  );
};

export default Cart;
