import React, { useEffect } from "react";
import { useContext, useState } from "react";
import { GlobalState } from "../../GlobalState";
import axios from "axios";
import PaypalButton from "./PaypalButton";
import "./Cart.css";
import Navbar from "../../components/Navbar/Navbar";

const Cart = () => {
  const state = useContext(GlobalState);
  const [cart, setCart] = state.userApi.cart;
  const [token] = state.token;
  const [total, setTotal] = useState(0);

  useEffect(() => {
    const getTotal = () => {
      const total = cart.reduce((prev, item) => {
        return prev + item.precio * item.quantity;
      }, 0);

      setTotal(total);
    };

    getTotal();
  }, [cart]);

  const addCart = async () => {
    await axios.patch(
      "/usuario/addCart",
      { cart },
      {
        headers: { Autorizacion: token },
      }
    );
  };

  const increment = (id) => {
    cart.forEach((item) => {
      if (item._id === id) {
        item.quantity += 1;
      }
    });
    setCart([...cart]);
    addCart();
  };

  const decrement = (id) => {
    cart.forEach((item) => {
      if (item._id === id) {
        item.quantity === 1 ? (item.quantity = 1) : (item.quantity -= 1);
      }
    });
    setCart([...cart]);
    addCart();
  };

  const tranSuccess = async (payment) => {
    console.log(payment);
  };
  const removeProduct = (id) => {
    if (window.confirm("Do you want to delete this product?")) {
      cart.forEach((item, index) => {
        if (item._id === id) {
          cart.splice(index, 1);
        }
      });

      setCart([...cart]);
      addCart();
    }
  };

  if (cart.length === 0)
    return (
      <h2 style={{ textAlign: "center", fontSize: "4rem" }}> Cart Empty </h2>
    );
  return (
    <>
    <Navbar />
    <div>
      <section className="section-pagetop bg">
        <div className="container">
          <h2 className="title-page">Shopping cart</h2>
        </div>
      </section>

      <section className="section-content padding-y">
        <div className="container">
          <div className="row">
            <main className="col-md-9">
              <div className="card">
                <table className="table table-borderless table-shopping-cart">
                  <thead className="text-muted">
                    <tr className="small text-uppercase">
                      <th scope="col">Producto</th>
                      <th scope="col" width="120">
                        Cantidad
                      </th>
                      <th scope="col" width="120">
                        Precio
                      </th>
                      <th scope="col" className="text-right" width="200">
                        {" "}
                      </th>
                    </tr>
                  </thead>
                  {cart.map((product) => (
                    <tbody>
                      <tr>
                        <td>
                          <figure className="itemside">
                            <div className="aside">
                              <img
                                src={product.imagen.url}
                                className="img-sm"
                              />
                            </div>
                            <figcaption className="info">
                              <a href="/#" className="title text-dark">
                                {product.nombre}
                              </a>
                              <p className="text-muted small">
                                Size: XL, Color: blue, <br /> Brand: Gucci
                              </p>
                            </figcaption>
                          </figure>
                        </td>
                        <td>
                          <div>
                            <div
                              className="btn btn-light"
                              onClick={() => increment(product._id)}
                            >
                              <i class="fas fa-plus-square"></i>
                            </div>
                            <span>{product.quantity}</span>
                            <div
                              className="btn btn-light mr-2"
                              onClick={() => decrement(product._id)}
                            >
                              <i class="fas fa-minus-square"></i>
                            </div>
                          </div>
                        </td>
                        <td>
                          <div className="price-wrap">
                            <var className="price">
                              s/. {product.precio * product.quantity}
                            </var>
                            <small className="text-muted">
                              s/. {product.precio} cada uno{" "}
                            </small>
                          </div>
                        </td>
                        <td className="form-control">
                          <a
                            data-original-title="Save to Wishlist"
                            title=""
                            href="/#"
                            className="btn btn-light mr-2"
                            data-toggle="tooltip"
                          >
                            {" "}
                            <i className="fa fa-heart"></i>
                          </a>
                          <div
                            className="btn btn-light btn-round"
