import React, { useEffect } from "react";
import { useContext, useState} from "react";
import { GlobalState } from "../../GlobalState";
import { Link } from "react-router-dom";
import "./Cart.css"

const Cart = () => {
  const state = useContext(GlobalState);
  const [cart, setCart] = state.userApi.cart;
  const [total, setTotal] = useState(0);

  useEffect(() => {
      const getTotal = () => {
          const total = cart.reduce((prev, item) => {
              return prev + (item.precio * item.quantity)
          }, 0)

          setTotal(total)
      }

      getTotal()
  }, [cart])

  const increment = (id) => {
      cart.forEach(item => {
          if(item._id === id) {
              item.quantity += 1
          }
      })
      setCart([...cart])
  }

  const decrement = (id) => {
    cart.forEach(item => {
        if(item._id === id) {
            item.quantity === 1 ? item.quantity = 1 : item.quantity -=1
        }
    })
    setCart([...cart])
}

const removeProduct = id => {
    if(window.confirm("Do you want to delete this product?")){
        cart.forEach((item, index) => {
            if(item._id === id) {
                cart.splice(index, 1)
            }
        })

        setCart([...cart])
    }
}



  if (cart.length === 0)
    return (
      <h2 style={{ textAlign: "center", fontSize: "4rem" }}> Cart Empty </h2>
    );
  return (
    <div>
      {cart.map((product) => (
        <div className="detail" key={product._id}>
          <img src={product.imagen.url} alt="" />

          <div className="box-detail">  
            <div className="row">
              <h2>{product.nombre}</h2>
            </div>

            <h3>s/.{product.precio * product.quantity}</h3>
            <p>{product.descripcion}</p>
            <p>{product.contenido}</p>

            <div className="amount">
                <button onClick={() => decrement(product._id)}>
                    -
                </button>
                <span> {product.quantity} </span>
                <button onClick={() => increment(product._id)}>
                    +
                </button>
            </div>
            {/* <p>Sold: {product.sold}</p> */}
            {/* <Link to="/cart" className="cart">
              Comprar
            </Link> */}
            <div className="delete"> x </div>
          </div>
        </div>
      ))}

      <div className="total">
          <h3>s/. {total} </h3>
          <Link to="#!"> Pagar </Link>
      </div>
    </div>
  );
};

export default Cart;
