import React, { useContext, useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { GlobalState } from "../../../GlobalState";
import ProductItem from "../utils/productItem/ProductItem";

const DetailProduct = () => {
  const params = useParams();
  const state = useContext(GlobalState);
  const [products] = state.productsApi.products;
  const [detailProduct, setDetailProduct] = useState([]);

  useEffect(() => {
    if (params.id) {
      products.forEach((product) => {
        if (product._id === params.id) setDetailProduct(product);
      });
    }
  }, [params.id, products]);
  console.log(detailProduct);
  if (detailProduct.length === 0) return null;

  return (
      <>
        <div className="detail">
            <img src={detailProduct.imagen.url} alt="" />
            <div className="box-detail">
                <div className="row">
                    <h2>{detailProduct.nombre}</h2>
                    {/* <h6>{detailProduct.producto_id}</h6> */}
                </div>
                <span>s/.{detailProduct.precio}</span>
                <p>{detailProduct.descripcion}</p>
                <p>{detailProduct.contenido}</p>
                <p>Sold: {detailProduct.sold}</p>
                <Link to="/cart" className="cart">  
                Comprar
                </Link>
            </div>
        </div>
        <div>
            <h2> Producto relacionado </h2>
            <div className="products">
                {
                    products.map( product => {
                        return product.categoria == detailProduct.categoria
                        ? <ProductItem key={product._id} product={product} /> : null
                    })
                }

            </div>

        </div>
    </>
  );
};

export default DetailProduct;
