import '../../../src/App.css'

import { Button } from "@mui/material";
import React, { useState } from "react";
import {Link} from 'react-router-dom';
import axios from 'axios';
import './SingUp.css'


export const SingUp = () => {
  const [user, setUser] = useState({
    email: '',
    contraseña: ''
  });

  const handleInput = e => {
      const {name, value} = e.target;
      setUser({...user, [name]:value})
  }

  const handleLoginSubmit = async e => {
      e.preventDefault()
      try {
          await axios.post('/usuario/login', {...user})

          localStorage.setItem('firstLogin', true)
          window.location.href = "/"
      } catch (err) {
          alert(err.response.data.msg)
      }
  }

  return (
    // <div className="login-page">
    //   <form onSubmit={handleLoginSubmit}>
    //       <h1> Login </h1>
    //     <input
    //       type="email"
    //       name="email"
    //       required
    //       placeholder="Email"
    //       value={user.email}
    //       onChange={handleInput}
    //     />

    //     <input
    //       type="contraseña"
    //       name="contraseña"
    //       required
    //       autoComplete="on"
    //       placeholder="Contraseña"
    //       value={user.contraseña}
    //       onChange={handleInput}

    //     />

    //     <div className="row">
    //       <Button type="submit"> Login </Button>
    //       <Link to="/register"> Register </Link> 
    //     </div>
    //   </form>
    // </div>

<div ng-app="validationApp" ng-controller="mainController" >
<div class="container">
  <div class="login-form">
    <h1>Member Login</h1>
    <form name="loginForm" ng-submit="submitForm()" novalidate>
      <div ng-class="{ 'has-error' : loginForm.name.$invalid && !loginForm.name.$pristine }">
        <input type="text" name="username" placeholder="Username" minlength="3" ng-model="user.username" required/>
        <p class="error-text" ng-if="loginForm.username.$invalid && loginForm.name.$pristine">You name is required.</p>
        <p class="help-block error-text" ng-if="loginForm.username.$error.minlength">Username is too short.</p>
      </div>
      <input type="password" minlength="6" name="password" ng-model="user.password" placeholder="Password" required/>
       <p class="help-block error-text" ng-if="loginForm.password.$minlength">Password is too short.</p>
      <button class="login-btn" ng-disabled="loginForm.$invalid">Login</button>
    </form>

    <ul>
      <li><a href="">Forgot Password?</a></li>
      <li><a href="">Register</a></li>
    </ul>
  </div>
  <div class="img-content">
   
    <p>Your perfect place to buy & sell digital goods</p>
  </div>
</div>
</div>
  );
};
