import React, {useState,useEffect, useContext} from 'react';
import {Link} from 'react-router-dom';
import { GlobalState } from '../../GlobalState';
import {Button} from '../Button-SignUp/Button';
import './NavBar.css'
// import fondoNegro_letraBlanco_chiquis from '../../../src/assets/images/fondoNegro_letraBlanco_chiquis.png'
// import loggo11 from '../../../src/assets/images/loggo11.png'

// import images from "../../assets/images";

function Navbar() {
    //   hooks usestate
    
    const[click, setClick] = useState(false);
    const[button, setButton] = useState(true);

    //sesion
    const state = useContext(GlobalState);
    // console.log(state);
    const [isLogged, setIsLogged] = state.userApi.isLogged
    const [isAdmin, setIsAdmin] = state.userApi.isAdmin

    const adminRouter = () => {
        return (
            <>
            <li><Link to="/create_product">Create Product</Link></li>
            <li><Link to="/caterogry">Categorias</Link></li>
            </>
        )
    }
    
    const loggedRouter = () => {
        return (
            <>
            <li><Link to="/history">History</Link></li>
            <li><Link to="/"> Logout </Link></li>
            </>
        )
    }

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () =>{
        if(window.innerWidth <= 960){
            setButton(false);
        } else{
            setButton(true);
        }
    };

    useEffect(()=>{
        showButton();
    }, []);
    
    window.addEventListener('resize',showButton);
    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
                        {/* <img src={loggo11} alt=""/> */}
                        {isAdmin ? 'LADLOLA admin' : 'LADLOLA'} <i className="fab fa-typo3"></i>
                    </Link>s
                    <div className="menu-icon" onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                    </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                        <li className="nav-item">
                            <Link to="/" className="nav-links" onClick={closeMobileMenu}>
                                Home
                            </Link>
                        </li>
                       <li className="nav-item">
                            <Link to="/services" className="nav-links" onClick={closeMobileMenu}>
                                Services
                            </Link>
                        </li>
                       <li className="nav-item">
                            <Link to="/products" className="nav-links" onClick={closeMobileMenu}>
                                Products
                            </Link>
                        </li> 
                       <li className="nav-item">
                            <Link to="/cart" className="nav-links" onClick={closeMobileMenu}>
                                Shopping Cart
                            </Link>
                        </li> 
                        <li>
                            <Link to="/sign-up" className="nav-links-mobile" onClick={closeMobileMenu}>
                                Sign Up
                            </Link>
                        </li> 
                    </ul>
                        {button && <Button className="btn--outline">Sign Up</Button>}
                </div>    
            </nav>   
        </>
    )
}

export default Navbar
